// nightland/src/modules/gameLoop.js
import { updateCombatDialogs } from './utils';
import * as text from '../assets/copy/textcontent.js';

export function handleMovePlayer(state, dispatch, key, showDialog) {
    let { x, y } = state.player;
    const gridWidth = 400;
    const gridHeight = 400;

    switch (key) {
        case 'ArrowUp':
            if (y < gridHeight - 1) y++;
            break;
        case 'ArrowDown':
            if (y > 0) y--;
            break;
        case 'ArrowLeft':
            if (x > 0) x--;
            break;
        case 'ArrowRight':
            if (x < gridWidth - 1) x++;
            break;
        default:
            return;
    }

    const playerMoved = x !== state.player.x || y !== state.player.y;
    if (playerMoved) {
        dispatch({
            type: 'UPDATE_PLAYER_POSITION',
            payload: { x, y }
        });
        checkEncounters(state, dispatch, showDialog);
    }
}

function checkEncounters(state, dispatch, showDialog) {
    const playerPixelX = state.player.x * 40; // tileSize 40px from gameState.js
    const playerPixelY = state.player.y * 40;
    const activeMonsters = state.activeMonsters.filter(m => m.active && m.hp > 0);

    // Check Watcher encounter (simplified)
    if (Math.abs(playerPixelX - (state.greatPowers[0]?.x || 0) * 40) < 160 && // Watcher's width in pixels
        Math.abs(playerPixelY - (state.greatPowers[0]?.y || 0) * 40) < 160) { // Height
        showDialog(text.watcherDeathMessage, 10000);
        setTimeout(() => {
            dispatch({
                type: 'UPDATE_PLAYER_HP',
                payload: { hp: 100 } // Reset HP
            });
            dispatch({
                type: 'UPDATE_PLAYER_POSITION',
                payload: { x: state.redoubt.x, y: state.redoubt.y }
            });
            showDialog(text.deathReviveMessage, 3600);
        }, 10800);
        return;
    }

    // Check Abhuman encounter
    for (const monster of activeMonsters) {
        if (Math.abs(playerPixelX - monster.x * 40) <= 40 && // tileSize
            Math.abs(playerPixelY - monster.y * 40) <= 40 && !state.inCombat) {
            handleCombatStart(state, dispatch, showDialog);
            break;
        }
    }

    // Spawn monsters periodically (simplified)
    if ((state.moveCount || 0) % (state.spawnThreshold || 7) === 0) {
        spawnMonsterNearPlayer(state, dispatch);
        updateMoveCount(state, dispatch, 0);
        updateSpawnThreshold(state, dispatch, Math.floor(Math.random() * 7) + 4);
    } else {
        updateMoveCount(state, dispatch, (state.moveCount || 0) + 1);
    }
}

function handleCombatStart(state, dispatch, showDialog) {
    const attackers = state.activeMonsters.slice(0, state.maxAttackers).map((monster, i) => ({
        monster,
        position: { x: state.player.x + (i % 2 ? 1 : -1), y: state.player.y + (i < 2 ? -1 : 1) }
    }));
    const dialogData = updateCombatDialogs(
        text.combatStartPlayerComment,
        attackers.map(() => text.combatStartEnemyComment),
        state.player,
        attackers.map(slot => slot.monster)
    );
    dispatch({
        type: 'SET_COMBAT',
        payload: {
            inCombat: true,
            attackSlots: attackers,
            turnOrder: [state.player, ...attackers.map(slot => slot.monster)],
            combatTurn: state.player,
            dialogData: dialogData
        }
    });
}

export function handleCombatStep(state, dispatch, showDialog) {
    if (!state.inCombat || !state.turnOrder.length) return;

    const current = state.combatTurn;
    const currentIndex = state.turnOrder.indexOf(current);
    const activeMonsters = state.attackSlots.map(slot => slot.monster).filter(m => m && m.hp > 0);
    let allMonsters = state.attackSlots.map(slot => slot.monster);

    console.log('Combat Step - Current:', current?.name, 'Turn Order:', state.turnOrder.map(t => t.name), 'Active Monsters:', activeMonsters.map(m => `${m.name} HP:${m.hp}`));

    let playerComment = "";
    let enemyComments = allMonsters.map(() => "");
    let updatedPlayer = { ...state.player };
    let updatedAttackSlots = [...state.attackSlots];

    if (current.name === state.player.name) {
        const target = activeMonsters[0];
        if (target) {
            if (Math.random() < 0.8) {
                target.hp -= 6;
                playerComment = text.combatPlayerHitComment;
                enemyComments[0] = target.hp <= 0 ? text.combatVictoryEnemyComment : "";
            } else {
                playerComment = text.combatPlayerMissComment;
            }
        }
    } else if (activeMonsters.includes(current)) {
        const monsterIndex = allMonsters.indexOf(current);
        if (Math.random() < 0.5) {
            updatedPlayer.hp -= 4;
            enemyComments[monsterIndex] = text.combatEnemyHitComment;
            allMonsters[monsterIndex].lastComment = text.combatEnemyHitComment;
        } else {
            enemyComments[monsterIndex] = text.combatEnemyMissComment;
            allMonsters[monsterIndex].lastComment = text.combatEnemyMissComment;
        }
    }

    const dialogData = updateCombatDialogs(playerComment, enemyComments, updatedPlayer, allMonsters);

    const nextTurnIndex = (currentIndex + 1) % state.turnOrder.length;
    const nextTurn = state.turnOrder[nextTurnIndex];

    if (updatedPlayer.hp <= 0) {
        endCombat(state, dispatch, showDialog);
    } else if (activeMonsters.length === 0) {
        endCombat(state, dispatch, showDialog);
    } else {
        if (current === state.turnOrder[state.turnOrder.length - 1]) {
            resolveRound(state, dispatch);
            updatedAttackSlots = state.attackSlots.filter(s => s.monster && s.monster.hp > 0);
            calculateTurnOrder(state, dispatch);
        }

        dispatch({
            type: 'SET_COMBAT',
            payload: {
                inCombat: true,
                attackSlots: updatedAttackSlots,
                turnOrder: state.turnOrder,
                combatTurn: nextTurn,
                player: { ...updatedPlayer, lastComment: playerComment },
                dialogData: dialogData
            }
        });
        console.log('After Dispatch - inCombat:', state.inCombat, 'Next Turn:', nextTurn?.name);
    }
}

function calculateTurnOrder(state, dispatch) {
    const combatants = [state.player, ...state.attackSlots.map(slot => slot.monster).filter(m => m && m.hp > 0)];
    const newTurnOrder = combatants.filter(c => c.hp > 0);
    dispatch({
        type: 'SET_COMBAT',
        payload: {
            inCombat: state.inCombat,
            attackSlots: state.attackSlots,
            turnOrder: newTurnOrder,
            combatTurn: newTurnOrder[0],
            player: state.player,
            dialogData: state.dialogData
        }
    });
}

function resolveRound(state, dispatch) {
    const deadMonsters = state.attackSlots.filter(slot => slot.monster && slot.monster.hp <= 0);
    if (deadMonsters.length > 0) {
        deadMonsters.forEach(slot => {
            slot.monster.active = false;
            if (state.waitingMonsters.length > 0) {
                const nextMonster = state.waitingMonsters.shift();
                nextMonster.x = slot.position.x;
                nextMonster.y = slot.position.y;
                slot.monster = nextMonster;
            } else {
                slot.monster = null;
            }
        });
        const newAttackSlots = state.attackSlots.filter(s => s.monster && s.monster.hp > 0);
        dispatch({
            type: 'SET_COMBAT',
            payload: {
                inCombat: true,
                attackSlots: newAttackSlots,
                dialogData: state.dialogData
            }
        });
    }
}

function endCombat(state, dispatch, showDialog) {
    dispatch({
        type: 'SET_COMBAT',
        payload: {
            inCombat: false,
            attackSlots: [],
            waitingMonsters: [],
            turnOrder: [],
            combatTurn: null,
            player: { ...state.player, lastComment: "" },
            dialogData: updateCombatDialogs("", [], state.player, [])
        }
    });
    showDialog(text.combatVictoryPlayerComment, 3600);
}

function spawnMonsterNearPlayer(state, dispatch) {
    const monsterTemplate = state.monsters.find(m => m.name === 'Abhuman');
    const spawnDistance = Math.ceil(Math.random() * 3) + 1;
    const direction = Math.floor(Math.random() * 4);
    const newMonster = { ...monsterTemplate, active: true };
    // eslint-disable-next-line default-case
    switch (direction) {
        case 0: // Up
            newMonster.x = state.player.x;
            newMonster.y = Math.max(0, state.player.y - spawnDistance);
            break;
        case 1: // Down
            newMonster.x = state.player.x;
            newMonster.y = Math.min(199, state.player.y + spawnDistance);
            break;
        case 2: // Left
            newMonster.x = Math.max(0, state.player.x - spawnDistance);
            newMonster.y = state.player.y;
            break;
        case 3: // Right
            newMonster.x = Math.min(99, state.player.x + spawnDistance);
            newMonster.y = state.player.y;
            break;
    }
    dispatch({
        type: 'ADD_ACTIVE_MONSTER',
        payload: { monster: newMonster }
    });
}

export function updateMoveCount(state, dispatch, count) {
    dispatch({ type: 'UPDATE_MOVE_COUNT', payload: { moveCount: count } });
}

export function updateSpawnThreshold(state, dispatch, threshold) {
    dispatch({ type: 'UPDATE_SPAWN_THRESHOLD', payload: { spawnThreshold: threshold } });
}
