// nightland/src/App.js
import React, { useReducer, useState, useEffect, useRef, useCallback } from 'react';
import { initialState, reducer } from './modules/gameState';
import SplashScreen from './components/SplashScreen';
import PrincessScreen from './components/PrincessScreen';
import StatusBar from './components/StatusBar';
import CombatUI from './components/CombatUI';
import GameDialog from './components/GameDialog';
import { handleMovePlayer, handleCombatStep } from './modules/gameLoop';
import './styles/styles.css';

const App = () => {
    const [state, dispatch] = useReducer(reducer, initialState);
    const [phase, setPhase] = useState('splash');
    const [showSettings, setShowSettings] = useState(false);
    const [dialogMessage, setDialogMessage] = useState('');
    const gameContainerRef = useRef(null);

    console.log('App State:', state); // Log state to check positions

    const handleStartGame = () => setPhase('princess');
    const handlePrincessNext = () => setPhase('gameplay');
    const toggleSettings = (e) => {
        e.stopPropagation();
        setShowSettings(!showSettings);
    };

    const showDialog = useCallback((message, duration = 3600) => {
        setDialogMessage(message);
        setTimeout(() => setDialogMessage(''), duration);
    }, []); // Wrap in useCallback

    const renderMap = useCallback(() => {
        if (!gameContainerRef.current) {
            console.log('Game container ref not found'); // Log
            return;
        }
        console.log('Rendering map elements:', {
            player: state.player,
            redoubt: state.redoubt,
            monsters: state.activeMonsters,
            watcher: state.greatPowers[0]
        });
        scrollPlayerIntoView();
    }, [state]); // Add state as dependency

    const scrollPlayerIntoView = useCallback(() => {
        if (!gameContainerRef.current) {
            console.log('Container ref missing in scroll'); // Log
            return;
        }
        const container = gameContainerRef.current;
        const player = container.querySelector('#player');
        const redoubt = container.querySelector('#redoubt');
        const watcher = container.querySelector('#watcher');
        console.log('DOM Elements Found:', { player, redoubt, watcher, playerStyles: player?.style, redoubtStyles: redoubt?.style, watcherStyles: watcher?.style }); // Detailed logging
        if (!player || !redoubt) {
            console.log('Player or Redoubt not found in DOM:', { player, redoubt }); // Log
            return;
        }

        const containerRect = container.getBoundingClientRect();
        const playerRect = player.getBoundingClientRect();
        const redoubtRect = redoubt.getBoundingClientRect();
        const containerHeight = containerRect.height;
        const containerWidth = containerRect.width;
        const halfwayPoint = containerHeight / 2;

        let scrollY = 0;
        const playerBottom = playerRect.bottom - containerRect.top;
        const playerLeft = playerRect.left - containerRect.left;

        console.log('Scrolling debug:', { playerBottom, playerLeft, halfwayPoint, containerHeight, containerWidth, scrollY }); // Enhanced logging

        // Force initial positioning to show redoubt at bottom and player centered within viewport
        scrollY = Math.max(0, Math.min(container.scrollHeight - containerHeight - (redoubtRect.height + 34), containerHeight - redoubtRect.height - 34)); // Show redoubt at bottom, within viewport
        let scrollLeft = Math.max(0, (state.player.x * 40) - (containerWidth / 2)); // Center player horizontally, ensure within bounds
        scrollLeft = Math.min(scrollLeft, container.scrollWidth - containerWidth); // Prevent over-scrolling right

        // Ensure scroll values are within bounds
        scrollY = Math.max(0, Math.min(scrollY, container.scrollHeight - containerHeight));
        scrollLeft = Math.max(0, Math.min(scrollLeft, container.scrollWidth - containerWidth));

        // Use requestAnimationFrame for smooth scrolling and force visibility
        requestAnimationFrame(() => {
            container.scrollLeft = scrollLeft;
            container.scrollTop = scrollY;
            console.log('Applied scroll:', { scrollLeft, scrollTop: scrollY, containerScrollHeight: container.scrollHeight, containerScrollWidth: container.scrollWidth, containerOffsetLeft: container.offsetLeft, containerOffsetTop: container.offsetTop });

            // Force inline styles for debugging
            player.style.border = '5px solid red'; // Bright red border
            player.style.background = `url('../../public/assets/images/player.png') no-repeat center center / cover`; // Force background
            player.style.position = 'absolute'; // Ensure absolute positioning
            player.style.visibility = 'visible'; // Ensure visible
            player.style.opacity = '1'; // Ensure full opacity

            redoubt.style.border = '5px solid red'; // Bright red border
            redoubt.style.background = `url('../../public/assets/images/redoubt.png') no-repeat center center / cover`; // Force background
            redoubt.style.position = 'absolute'; // Ensure absolute positioning
            redoubt.style.visibility = 'visible'; // Ensure visible
            redoubt.style.opacity = '1'; // Ensure full opacity

            watcher.style.border = '5px solid red'; // Bright red border
            watcher.style.background = `url('../../public/assets/images/watcherse.png') no-repeat center center / cover`; // Force background
            watcher.style.position = 'absolute'; // Ensure absolute positioning
            watcher.style.visibility = 'visible'; // Ensure visible
            watcher.style.opacity = '1'; // Ensure full opacity
        });
    }, [state]); // Add state as dependency

    useEffect(() => {
        if (phase === 'gameplay') {
            renderMap();
        }
    }, [phase, renderMap]); // Updated dependency

    // Ensure initial scroll and handle resize/zoom consistently
    useEffect(() => {
        if (phase === 'gameplay') {
            // Delay scroll to ensure DOM is fully rendered, with requestAnimationFrame
            const timer = setTimeout(() => {
                requestAnimationFrame(() => scrollPlayerIntoView());
            }, 1000); // Increased delay to 1s for robustness

            const handleResize = () => {
                requestAnimationFrame(() => scrollPlayerIntoView()); // Reapply scroll on window resize/zoom
            };

            window.addEventListener('resize', handleResize);
            return () => {
                clearTimeout(timer); // Cleanup timeout
                window.removeEventListener('resize', handleResize); // Cleanup
                // Remove temporary debugging styles on unmount
                const player = document.querySelector('#player');
                const redoubt = document.querySelector('#redoubt');
                const watcher = document.querySelector('#watcher');
                if (player) {
                    player.style.border = '';
                    player.style.background = '';
                    player.style.position = '';
                    player.style.visibility = '';
                    player.style.opacity = '';
                }
                if (redoubt) {
                    redoubt.style.border = '';
                    redoubt.style.background = '';
                    redoubt.style.position = '';
                    redoubt.style.visibility = '';
                    redoubt.style.opacity = '';
                }
                if (watcher) {
                    watcher.style.border = '';
                    watcher.style.background = '';
                    watcher.style.position = '';
                    watcher.style.visibility = '';
                    watcher.style.opacity = '';
                }
            };
        }
    }, [phase, scrollPlayerIntoView]); // Depend on phase and scrollPlayerIntoView

    useEffect(() => {
        const handleKeyDown = (event) => {
            if (phase === 'gameplay') {
                event.preventDefault();
                if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'].includes(event.key)) {
                    console.log('Moving player with key:', event.key); // Log movement
                    handleMovePlayer(state, dispatch, event.key, showDialog);
                } else if (event.key === ' ' && state.inCombat) {
                    console.log('Combat step triggered'); // Log combat
                    handleCombatStep(state, dispatch, showDialog);
                }
            }
        };
        document.addEventListener('keydown', handleKeyDown);
        return () => document.removeEventListener('keydown', handleKeyDown);
    }, [state, phase, showDialog]);

    return (
        <div className="app">
            {phase === 'splash' && <SplashScreen onStart={handleStartGame} />}
            {phase === 'princess' && <PrincessScreen onNext={handlePrincessNext} />}
            {phase === 'gameplay' && (
                <div id="gameplay-screen" ref={gameContainerRef}>
                    <div className="game-board">
                        <div id="redoubt" />
                        <div
                            id="player"
                            style={{
                                left: `${state.player.x * 40}px`,
                                bottom: `${state.player.y * 40}px`,
                                border: '5px solid red', // Temporary inline border for debugging
                                background: `url('../../public/assets/images/player.png') no-repeat center center / cover`, // Temporary inline background
                                position: 'absolute', // Ensure absolute positioning
                                visibility: 'visible', // Ensure visible
                                opacity: '1' // Ensure full opacity
                            }}
                        />
                        {state.activeMonsters.filter(m => m.active && m.hp > 0).map((monster, index) => (
                            <div
                                key={index}
                                className="monster"
                                style={{
                                    left: `${monster.x * 40}px`,
                                    bottom: `${monster.y * 40}px`,
                                    border: '5px solid red', // Temporary inline border for debugging
                                    background: `url('../../public/assets/images/abhuman.png') no-repeat center center / cover`, // Temporary inline background
                                    position: 'absolute', // Ensure absolute positioning
                                    visibility: 'visible', // Ensure visible
                                    opacity: '1' // Ensure full opacity
                                }}
                            />
                        ))}
                        <div
                            id="watcher"
                            style={{
                                left: `${state.greatPowers[0]?.x * 40 || 0}px`,
                                bottom: `${state.greatPowers[0]?.y * 40 || 0}px`,
                                border: '5px solid red', // Temporary inline border for debugging
                                background: `url('../../public/assets/images/watcherse.png') no-repeat center center / cover`, // Temporary inline background
                                position: 'absolute', // Ensure absolute positioning
                                visibility: 'visible', // Ensure visible
                                opacity: '1' // Ensure full opacity
                            }}
                        />
                    </div>
                    <StatusBar hp={state.player.hp} onSettingsToggle={toggleSettings} />
                    {state.inCombat && <CombatUI state={state} dispatch={dispatch} />}
                </div>
            )}
            <GameDialog message={dialogMessage} />
            <audio id="background-audio" loop autoPlay>
                <source src="/assets/sounds/ambient-background.wav" type="audio/wav" />
            </audio>
        </div>
    );
};

export default App;
