import { useEffect, useState } from "react";

const GameLoop = () => {
  const [playerPosition, setPlayerPosition] = useState({ x: 100, y: 300 });
  const [offsetY, setOffsetY] = useState(0);

  useEffect(() => {
    const handleKeyPress = (event) => handleMovePlayer(event, setPlayerPosition, setOffsetY);

    window.addEventListener("keydown", handleKeyPress);
    return () => window.removeEventListener("keydown", handleKeyPress);
  }, []);

  return (
    <div id="game-container" style={{ position: "relative", overflow: "hidden", height: "100vh" }}>
      <div
        id="game-world"
        style={{ transform: `translateY(-${offsetY}px)`, transition: "transform 0.1s linear" }}
      >
        <div
          id="player"
          style={{ position: "absolute", left: playerPosition.x, top: playerPosition.y }}
        >
          üèÉ‚Äç‚ôÇÔ∏è
        </div>
        <div id="redoubt" style={{ position: "absolute", bottom: 0, width: "100%" }}>
          üè∞
        </div>
      </div>
    </div>
  );
};

export const handleMovePlayer = (event, setPlayerPosition, setOffsetY) => {
  setPlayerPosition((prev) => {
    let newX = prev.x;
    let newY = prev.y;

    switch (event.key) {
      case "ArrowUp":
        newY -= 10;
        break;
      case "ArrowDown":
        newY += 10;
        break;
      case "ArrowLeft":
        newX -= 10;
        break;
      case "ArrowRight":
        newX += 10;
        break;
      default:
        break;
    }

    if (newY < 200) {
      setOffsetY((prevOffset) => prevOffset + (200 - newY));
      newY = 200;
    }

    return { x: newX, y: newY };
  });
};

export const handleCombatStep = () => {
  console.log("Combat step executed");
};

export default GameLoop;
