// nightland/src/components/GameLoop.js
import React, { useReducer } from 'react';
import * as text from '../assets/copy/textcontent.js';
import { initialState, reducer } from '../modules/gameState';
import Combat from './Combat';
import Player from './Player';

const GameLoop = () => {
    const [state, dispatch] = useReducer(reducer, initialState);

    const handleTestDamage = () => {
        dispatch({ type: 'UPDATE_PLAYER_HP', payload: { hp: state.player.hp - 10 } });
    };

    const handleStartCombat = () => {
        const attackers = state.activeMonsters.slice(0, state.maxAttackers).map((monster, i) => ({
            monster,
            position: { x: state.player.x + (i % 2 ? 1 : -1), y: state.player.y + (i < 2 ? -1 : 1) }
        }));
        dispatch({
            type: 'SET_COMBAT',
            payload: {
                inCombat: true,
                attackSlots: attackers,
                turnOrder: [state.player, ...attackers.map(slot => slot.monster)],
                combatTurn: state.player
            }
        });
    };

    return (
        <div className="game-loop">
            <h1>The Nightland</h1>
            <Player position={state.player} hp={state.player.hp} />
            <p>Player HP: {state.player.hp}</p>
            <button onClick={handleTestDamage}>Take 10 Damage</button>
            <button onClick={handleStartCombat}>Start Combat</button>
            {state.inCombat && <Combat state={state} dispatch={dispatch} />}
        </div>
    );
};

export default GameLoop;
