// nightland/src/components/SplashScreen.js
import React from 'react';
import splashImage from '../assets/images/splashscreen.png';

const SplashScreen = ({ onStart }) => (
    <div id="splash">
        <img src={splashImage} alt="Splash Screen" />
        <button
            id="splash-button"
            style={{
                position: 'absolute',
                bottom: '20px',
                left: '50%',
                transform: 'translateX(-50%)',
                width: '100px',
                height: '40px',
                border: 'none',
                cursor: 'pointer'
            }}
            onClick={onStart}
        />
    </div>
);

export default SplashScreen;
