const Dialog = (props) => {
    const { data, index } = props;
    const className = index === 'player' ? 'combat-player' : `combat-enemy-${index + 1} ${data.dead ? 'dead' : ''}`;
    console.log(`Dialog ${index} - Props:`, props);
    console.log(`Dialog ${index} - Data:`, data);
    return (
        <div className={className} style={{ minHeight: '60px', whiteSpace: 'pre-wrap' }}>
            {data.name}<br />
            HP: {data.hp}<br />
            <span style={{ color: 'yellow', display: 'block' }}>{data.comment || 'No comment'}</span>
        </div>
    );
};
