// nightland/src/App.js
import React, { useState, useEffect } from 'react';
import './styles/styles.css';

const App = () => {
    const [phase, setPhase] = useState('splash');

    console.log('App Phase:', phase); // Basic logging

    const handleStartGame = () => setPhase('princess');
    const handlePrincessNext = () => setPhase('gameplay');

    useEffect(() => {
        console.log('Phase changed to:', phase);
        if (phase === 'gameplay') {
            const testImage = document.querySelector('#test-image');
            console.log('Test Image Element:', { testImage, styles: testImage?.style.cssText });

            // Test image loading
            const img = new Image();
            img.src = '/assets/images/player.png';
            img.onload = () => console.log('Image loaded successfully:', img.src);
            img.onerror = () => console.log('Image failed to load:', img.src, 'Error:', img.error);
        }
    }, [phase]);

    return (
        <div className="app">
            {phase === 'splash' && (
                <div id="splash">
                    <button id="splash-button" onClick={handleStartGame}>
                        Start Game
                    </button>
                </div>
            )}
            {phase === 'princess' && (
                <div id="princess-screen">
                    <img src="/assets/images/sadprincess.png" alt="Princess Screen" />
                    <div id="dialog" className="princess-dialog">
                        <p>Help me, brave warrior! The Nightland threatens us all.</p>
                    </div>
                    <button id="princess-button" onClick={handlePrincessNext}>
                        Continue
                    </button>
                </div>
            )}
            {phase === 'gameplay' && (
                <div
                    id="test-image"
                    style={{
                        position: 'fixed', // Fixed to viewport
                        left: '0px', // Top-left corner
                        top: '0px', // Top-left corner
                        border: '5px solid red', // Bright red border for debugging
                        background: `url('/assets/images/player.png') no-repeat center center / cover`, // Use absolute path (working)
                        visibility: 'visible', // Ensure visible
                        opacity: '1', // Ensure full opacity
                        width: '40px', // Ensure size
                        height: '40px' // Ensure size
                    }}
                >
                    {/* Fallback text for debugging */}
                    <span style={{ color: 'white', position: 'absolute', top: '0', left: '0' }}>Test Image</span>
                </div>
            )}
            <audio id="background-audio" loop autoPlay>
                <source src="/assets/sounds/ambient-background.wav" type="audio/wav" />
            </audio>
        </div>
    );
};

export default App;
