// nightland/src/modules/gameState.js
export const initialState = {
    player: {
        position: { x: 1000, y: 1880 },
        hp: 100,
        name: "Christos",
        description: "One of the humans from the Last Redoubt.",
        initiative: 10,
        lastComment: ""
    },
    redoubt: {
        position: { x: 920, y: 1840 },
        name: "The Last Redoubt",
        description: "The last haven of mankind save one. It protects millions of souls from the horrors of the Nightland."
    },
    monsters: [{ /* ... */ }],
    greatPowers: [{ /* ... */ }],
    mapWidth: 400,
    mapHeight: 400,
    map: Array(400).fill().map(() => Array(400).fill()),
    activeMonsters: [],
    attackSlots: [],
    audioStarted: false,
    combatTurn: null,
    dialogData: { player: {}, enemies: [] },
    inCombat: false,
    maxAttackers: 4,
    moveCount: 0,
    spawnThreshold: 5,
    tileSize: 40,
    turnOrder: [],
    viewHeight: 800,
    waitingMonsters: []
};

export const reducer = (state, action) => {
    switch (action.type) {
        case 'MOVE_PLAYER':
            return { ...state, player: { ...state.player, position: action.payload.position } };
        case 'SET_AUDIO_STARTED':
            return { ...state, audioStarted: action.payload };
        default:
            return state;
    }
};
