// nightland/src/components/SplashScreen.js
import React from 'react';
import splashImage from '../assets/images/splashscreen.png';
import * as text from '../assets/copy/textcontent.js';

const SplashScreen = ({ onStart }) => (
    <div id="splash">
        <img
            src={splashImage}
            alt="Splash Screen"
            style={{
                display: 'block',
                margin: '0 auto', // Center horizontally
                height: '80vh',   // 80% of viewport height
                width: 'auto',    // Preserve aspect ratio
            }}
        />
        <button
            id="splash-button"
            style={{
                position: 'absolute',
                bottom: '20px',
                left: '50%',
                transform: 'translateX(-50%)',
                width: '100px',
                height: '40px',
                backgroundColor: '#8B0000',
                color: 'white',
                border: 'none',
                cursor: 'pointer',
                fontFamily: 'monospace'
            }}
            onClick={onStart}
        >
            {text.splashScreenButtonText}
        </button>
    </div>
);

export default SplashScreen;
