// nightland/src/components/Combat.js
import React, { useEffect, useState } from 'react';
import { updateCombatDialogs } from '../modules/utils';
import Dialog from './Dialog';

function calculateTurnOrder(state, dispatch) {
    const combatants = [state.player, ...state.attackSlots.map(slot => slot.monster).filter(m => m && m.hp > 0)];
    const newTurnOrder = combatants.filter(c => c.hp > 0);
    dispatch({
        type: 'SET_COMBAT',
        payload: {
            inCombat: state.inCombat,
            attackSlots: state.attackSlots,
            turnOrder: newTurnOrder,
            combatTurn: newTurnOrder[0]
        }
    });
}

function resolveRound(state, dispatch) {
    const activeMonsters = state.attackSlots.map(slot => slot.monster).filter(m => m && m.hp > 0);
    const deadMonsters = state.attackSlots.filter(slot => slot.monster && slot.monster.hp <= 0);

    if (deadMonsters.length > 0) {
        deadMonsters.forEach(slot => {
            slot.monster.active = false;
            if (state.waitingMonsters.length > 0) {
                const nextMonster = state.waitingMonsters.shift();
                nextMonster.x = slot.position.x;
                nextMonster.y = slot.position.y;
                slot.monster = nextMonster;
            } else {
                slot.monster = null;
            }
        });
        const newAttackSlots = state.attackSlots.filter(s => s.monster && s.monster.hp > 0);
        dispatch({ type: 'SET_COMBAT', payload: { inCombat: true, attackSlots: newAttackSlots } });
    }

    moveWaitingMonsters(state, dispatch);
    calculateTurnOrder(state, dispatch);
}

function moveWaitingMonsters(state, dispatch) {
    const activeMonsters = state.activeMonsters.filter(m => m.active && m.hp > 0);
    const allAttackSlotsFull = state.attackSlots.length >= state.maxAttackers;
    activeMonsters.forEach(monster => {
        if (monster.hp > 0 && !state.attackSlots.some(slot => slot.monster === monster)) {
            const dx = Math.abs(monster.x - state.player.x);
            const dy = Math.abs(monster.y - state.player.y);
            if (allAttackSlotsFull && (dx <= 2 || dy <= 2)) {
                if (!state.waitingMonsters.includes(monster)) {
                    dispatch({
                        type: 'UPDATE_WAITING_MONSTERS',
                        payload: { waitingMonsters: [...state.waitingMonsters, monster] }
                    });
                }
            }
        }
    });
}

function endCombat(state, dispatch) {
    dispatch({
        type: 'SET_COMBAT',
        payload: {
            inCombat: false,
            attackSlots: [],
            waitingMonsters: [],
            turnOrder: [],
            combatTurn: null
        }
    });
    roundNumber = 1;
}

let roundNumber = 1;

function combatStep(state, dispatch) {
    if (!state.inCombat || !state.turnOrder.length) return;

    const current = state.combatTurn;
    const currentIndex = state.turnOrder.indexOf(current);
    const activeMonsters = state.attackSlots.map(slot => slot.monster).filter(m => m && m.hp > 0);
    let allMonsters = state.attackSlots.map(slot => slot.monster);
    let playerComment = "";
    let enemyComments = allMonsters.map(() => "");

    console.log('Combat Step - Active Monsters:', activeMonsters, 'All Monsters:', allMonsters);

    if (currentIndex > 1 || roundNumber > 1) {
        updateCombatDialogs("", allMonsters.map(() => ""), state.player, allMonsters);
    }

    if (current === state.player) {
        const target = activeMonsters[0];
        console.log('Player Turn - Target:', target);
        if (target) {
            if (Math.random() < 0.8) {
                target.hp -= 6;
                playerComment = "You hit for 6 points!";
                enemyComments = allMonsters.map(slot => slot === target && slot.hp <= 0 ? "Monster is dead!" : slot.lastComment || "");
                if (target.hp <= 0) {
                    calculateTurnOrder(state, dispatch);
                }
            } else {
                playerComment = "You miss!";
            }
        } else {
            playerComment = "No target available!";
        }
    } else if (activeMonsters.includes(current)) {
        const monsterIndex = allMonsters.indexOf(current);
        const isFirstLivingMonster = currentIndex === 1 && allMonsters.some(m => m && m.hp <= 0);
        if (isFirstLivingMonster) {
            const newAttackSlots = state.attackSlots.filter(s => s.monster && s.monster.hp > 0);
            allMonsters = newAttackSlots.map(slot => slot.monster);
            dispatch({ type: 'SET_COMBAT', payload: { inCombat: true, attackSlots: newAttackSlots } });
            for (let i = 0; i < newAttackSlots.length; i++) {
                console.log(`Flicker would apply to combat-enemy-${i + 1}`);
            }
        }

        if (Math.random() < 0.5) {
            const newHp = state.player.hp - 4;
            enemyComments[monsterIndex] = "He hit you for 4 points!";
            allMonsters[monsterIndex].lastComment = "He hit you for 4 points!";
            dispatch({ type: 'UPDATE_PLAYER_HP', payload: { hp: newHp } });
        } else {
            enemyComments[monsterIndex] = "He missed you!";
            allMonsters[monsterIndex].lastComment = "He missed you!";
        }
    }

    console.log('Combat Step - Player Comment:', playerComment, 'Enemy Comments:', enemyComments);
    updateCombatDialogs(playerComment, enemyComments, state.player, allMonsters);

    if (state.player.hp <= 0) {
        updateCombatDialogs("You have perished!", activeMonsters.map(() => "Victory over the human!"), state.player, allMonsters);
        endCombat(state, dispatch);
    } else if (activeMonsters.length === 0 && state.waitingMonsters.length === 0) {
        updateCombatDialogs("Victory is yours!", allMonsters.map(() => ""), state.player, allMonsters);
        endCombat(state, dispatch);
    } else {
        dispatch({
            type: 'SET_COMBAT',
            payload: {
                inCombat: true,
                attackSlots: state.attackSlots,
                turnOrder: state.turnOrder,
                combatTurn: state.turnOrder[(currentIndex + 1) % state.turnOrder.length]
            }
        });
        moveWaitingMonsters(state, dispatch);

        if (current === state.turnOrder[state.turnOrder.length - 1]) {
            resolveRound(state, dispatch);
            calculateTurnOrder(state, dispatch);
            roundNumber++;
        }
    }
}

const Combat = ({ state, dispatch }) => {
    const [combatActive, setCombatActive] = useState(false);

    useEffect(() => {
        if (state.inCombat && !combatActive) {
            console.log('Combat Started - Initial State:', { combatTurn: state.combatTurn?.name, attackSlots: state.attackSlots });
            setCombatActive(true);
        }
    }, [state.inCombat]);

    const handleNextTurn = () => {
        console.log('Next Turn Clicked');
        combatStep(state, dispatch);
    };

    const dialogData = updateCombatDialogs("", state.attackSlots.map(slot => slot.monster.lastComment || ""), state.player, state.attackSlots.map(slot => slot.monster));
    console.log('Combat Render - Dialog Data:', dialogData);

    return (
        <div className="combat">
            <Dialog data={dialogData.player} index="player" />
            {dialogData.enemies.map((enemy, i) => enemy && (
                <Dialog key={i} data={enemy} index={i} />
            ))}
            <button onClick={handleNextTurn}>Next Turn</button>
        </div>
    );
};

export default Combat;
