// nightland/src/App.js
import React, { useState, useEffect } from 'react';
import './styles/styles.css';

const App = () => {
    const [phase, setPhase] = useState('splash');

    console.log('App Phase:', phase); // Basic logging

    const handleStartGame = () => setPhase('gameplay');

    useEffect(() => {
        console.log('Phase changed to:', phase);
    }, [phase]);

    return (
        <div className="app">
            {phase === 'splash' && (
                <div id="splash">
                    <button id="splash-button" onClick={handleStartGame}>
                        Start Game
                    </button>
                </div>
            )}
            {phase === 'gameplay' && (
                <div
                    id="test-image"
                    style={{
                        position: 'fixed', // Fixed to viewport
                        left: '0px', // Top-left corner
                        top: '0px', // Top-left corner
                        border: '5px solid red', // Bright red border for debugging
                        background: `url('../../public/assets/images/player.png') no-repeat center center / cover`, // Force background
                        visibility: 'visible', // Ensure visible
                        opacity: '1', // Ensure full opacity
                        width: '40px', // Ensure size
                        height: '40px' // Ensure size
                    }}
                />
            )}
            <audio id="background-audio" loop autoPlay>
                <source src="/assets/sounds/ambient-background.wav" type="audio/wav" />
            </audio>
        </div>
    );
};

export default App;
