// nightland/src/modules/gameState.js
const initialState = {
    player: {
        x: 8,
        y: 35,
        hp: 100,
        name: "Christos",
        description: "One of the humans from the Last Redoubt.",
        initiative: 10,
        lastComment: ""
    },
    monsters: [
        {
            x: 0,
            y: 0,
            hp: 20,
            name: "Abhuman",
            description: "Created from corrupted human stock ages ago, their hatred of humans is bone deep and drives them to kill.",
            active: false,
            type: "regular",
            initiative: 5,
            maxInstances: 4
        }
    ],
    greatPowers: [
        {
            x: 16,
            y: 0,
            width: 4,
            height: 4,
            name: "The Watcher of the South East",
            description: "One of the great powers and a source of great evil. It watches, silent, for Aeons.",
            type: "greatPower",
            initiative: 8,
            maxInstances: 1
        }
    ],
    redoubt: {
        name: "The Last Redoubt",
        description: "The last haven of mankind save one. It protects millions of souls from the horrors of the Nightland."
    },
    mapWidth: 20,
    mapHeight: 40,
    tileSize: 40,
    viewHeight: 800,
    map: Array(40).fill().map(() => Array(20).fill('.')),
    audioStarted: false,
    moveCount: 0,
    spawnThreshold: Math.floor(Math.random() * 7) + 4,
    inCombat: false,
    combatTurn: null,
    turnOrder: [],
    maxAttackers: 4,
    attackSlots: [],
    waitingMonsters: [],
    activeMonsters: [
        {
            x: 8,
            y: 35,
            hp: 20,
            name: "Abhuman",
            description: "Created from corrupted human stock ages ago, their hatred of humans is bone deep and drives them to kill.",
            active: true,
            type: "regular",
            initiative: 5,
            maxInstances: 4,
            lastComment: ""
        }
    ]
};

const reducer = (state, action) => {
    switch (action.type) {
        case 'UPDATE_PLAYER_POSITION':
            return {
                ...state,
                player: { ...state.player, x: action.payload.x, y: action.payload.y }
            };
        case 'UPDATE_PLAYER_HP':
            return {
                ...state,
                player: { ...state.player, hp: action.payload.hp }
            };
            case 'SET_COMBAT':
                return {
                    ...state,
                    inCombat: action.payload.inCombat,
                    attackSlots: action.payload.attackSlots || state.attackSlots,
                    turnOrder: action.payload.turnOrder || state.turnOrder,
                    combatTurn: action.payload.combatTurn || state.combatTurn,
                    player: action.payload.player || state.player,
                    dialogData: action.payload.dialogData || state.dialogData // Persist dialogData
                };
        case 'ADD_ACTIVE_MONSTER':
            return {
                ...state,
                activeMonsters: [...state.activeMonsters, action.payload.monster]
            };
        case 'UPDATE_WAITING_MONSTERS':
            return {
                ...state,
                waitingMonsters: action.payload.waitingMonsters
            };
        default:
            return state;
    }
};

export { initialState, reducer };
