import React, { useEffect, useReducer, useState, useRef } from 'react';
import { initializePositions, handleMovePlayer } from './modules/movement';
import { setupEventListeners } from './modules/events';
import { initialState, reducer } from './modules/gameState';

// Mock functions
const showDialog = (message) => console.log('Dialog:', message);
const audio = { play: () => Promise.resolve() };
const combatStep = () => console.log('Combat step');
const showInfoBox = (entity, infoBox) => console.log('Showing info for', entity, 'in', infoBox);
const toggleSettings = (event) => console.log('Toggling settings', event);
const toggleSFX = () => console.log('Toggling SFX');

function App() {
  const [state, dispatch] = useReducer(reducer, initialState);
  const [phase, setPhase] = useState('start');
  const gameContainerRef = useRef(null);
  const statusBarRef = useRef(null);
  const infoBoxRef = useRef(null);

  console.log('App.js: Component mounted, phase:', phase, 'state:', state);

  useEffect(() => {
    console.log('App.js: useEffect running, phase:', phase);
    if (phase === 'gameplay') {
      console.log('App.js: Initializing positions with state:', state);
      initializePositions(state);
    }

    // Setup event listeners
    const movePlayer = (key) => handleMovePlayer(state, dispatch, key, showDialog);
    const renderMap = () => console.log('Rendering map');
    const startGameWithPhase = (statusBar, gameContainer, renderMap) => {
      console.log('Starting game with phase transition');
      setPhase('gameplay');
    };
    const showPrincessScreenWithPhase = (statusBar) => {
      console.log('Showing princess screen with phase transition');
      setPhase('princess');
    };

    const statusBar = statusBarRef.current;
    const gameContainer = gameContainerRef.current;
    const infoBox = infoBoxRef.current;
    if (statusBar && gameContainer && infoBox) {
      console.log('App.js: Setting up event listeners with DOM elements');
      setupEventListeners(
        audio,
        statusBar,
        gameContainer,
        renderMap,
        movePlayer,
        combatStep,
        showPrincessScreenWithPhase,
        startGameWithPhase,
        showInfoBox,
        toggleSettings,
        toggleSFX,
        state,
        infoBox
      );
    } else {
      console.warn('App.js: DOM elements not found, delaying event setup');
    }
  }, [phase, state.player.position, state.redoubt.position, dispatch]);

  return (
    <div style={{ position: 'relative', width: '100vw', height: '100vh', overflow: 'hidden' }}>
      <h1>Debug: Nightland Game</h1>
      <p>Current Phase: {phase}</p>
      <div ref={gameContainerRef} id="game-container" style={{ position: 'relative', width: '2000px', height: '2000px', overflow: 'hidden' }}>
        <div className="game-board" style={{ position: 'absolute', width: '2000px', height: '2000px', outline: '1px solid blue' }}>
          <div id="player" style={{ position: 'absolute', width: '40px', height: '40px', background: 'yellow', outline: '1px solid yellow' }}></div>
          <div id="redoubt" style={{ position: 'absolute', width: '40px', height: '40px', background: 'green', outline: '1px solid green' }}></div>
        </div>
        <div ref={statusBarRef} id="status-bar" style={{ position: 'absolute', bottom: '0', width: '100%', height: '42px', background: 'red', outline: '1px solid red' }}>
          HP: {state.player.hp}
        </div>
        <div ref={infoBoxRef} id="info-box" style={{ position: 'absolute', top: '10px', right: '10px', width: '200px', height: '100px', background: 'white', border: '1px solid black', display: 'none' }}>
          Info Box
        </div>
      </div>
      {phase === 'start' && (
        <div id="splash-screen" style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }}>
          <button id="splash-button">Start Game</button>
        </div>
      )}
      {phase === 'princess' && (
        <div id="princess-screen" style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }}>
          <button id="princess-button">Begin</button>
        </div>
      )}
    </div>
  );
}

export default App;
