// nightland/src/components/SplashScreen.js
import React from 'react';
import splashImage from '../assets/images/splashscreen.png';
import * as text from '../assets/copy/textcontent.js';

const SplashScreen = ({ onStart }) => (
    <div id="splash">
        <img src={splashImage} alt="Splash Screen" />
        <button id="splash-button" onClick={onStart}>
            {text.splashScreenButtonText || 'Start'} {/* Fallback if not defined */}
        </button>
    </div>
);

export default SplashScreen;
