// nightland/src/App.js
import React, { useReducer, useState, useEffect } from 'react';
import { initialState, reducer } from './modules/gameState';
import SplashScreen from './components/SplashScreen';
import PrincessScreen from './components/PrincessScreen';
import StatusBar from './components/StatusBar';
import CombatUI from './components/CombatUI';
import GameDialog from './components/GameDialog';
import InfoBox from './components/InfoBox';
import redoubtImage from './assets/images/redoubt.png';
import playerImage from './assets/images/player.png';
import { handleTestDamage, handleStartCombat, handleMovePlayer } from './modules/gameLoop';
import './styles/styles.css';

const App = () => {
    const [state, dispatch] = useReducer(reducer, initialState);
    const [phase, setPhase] = useState('splash');
    const [showSettings, setShowSettings] = useState(false);
    const [dialogMessage, setDialogMessage] = useState('');
    const [infoObject, setInfoObject] = useState(null);

    const handleStartGame = () => setPhase('princess');
    const handlePrincessNext = () => setPhase('gameplay');
    const toggleSettings = (e) => {
        e.stopPropagation();
        setShowSettings(!showSettings);
    };
    const showDialog = (message) => {
        setDialogMessage(message);
        setTimeout(() => setDialogMessage(''), 3600);
    };
    const showInfoBox = (obj) => {
        setInfoObject(obj);
        if (obj) setTimeout(() => setInfoObject(null), 3600);
    };

    useEffect(() => {
        const handleKeyDown = (event) => {
            if (phase === 'gameplay' && !state.inCombat && ['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'].includes(event.key)) {
                event.preventDefault();
                handleMovePlayer(state, dispatch, event.key);
            }
        };
        document.addEventListener('keydown', handleKeyDown);
        return () => document.removeEventListener('keydown', handleKeyDown);
    }, [state, phase]);

    return (
        <div className="app">
            {phase === 'splash' && <SplashScreen onStart={handleStartGame} />}
            {phase === 'princess' && <PrincessScreen onNext={handlePrincessNext} />}
            {phase === 'gameplay' && (
                <div id="gameplay-screen">
                    <div className="game-board">
                        <img src={redoubtImage} alt="Redoubt" id="redoubt" />
                        <img
                            src={playerImage}
                            alt="Christos"
                            id="player"
                            style={{
                                left: `${state.player.x * 10}px`,
                                bottom: `${state.player.y * 10}px`
                            }}
                        />
                    </div>
                    <div id="status-bar">
                        <span id="hp-display">HP: {state.player.hp}</span>
                        <img
                            id="gear-icon"
                            src="https://cdn.jsdelivr.net/npm/bootstrap-icons@1.11.3/icons/gear-fill.svg"
                            alt="Settings"
                            onClick={toggleSettings}
                        />
                    </div>
                    <button id="test-damage" onClick={() => handleTestDamage(state, dispatch)}>Take 10 Damage</button>
                    <button id="start-combat" onClick={() => handleStartCombat(state, dispatch)}>Start Combat</button>
                    {state.inCombat && <CombatUI state={state} dispatch={dispatch} />}
                </div>
            )}
            <GameDialog message={dialogMessage} />
            <InfoBox object={infoObject} />
            <audio id="background-audio" loop autoPlay>
                <source src="/assets/sounds/ambient-background.wav" type="audio/wav" />
            </audio>
        </div>
    );
};

export default App;
