import { useEffect, useState } from "react";

const GameLoop = () => {
  const [playerPosition, setPlayerPosition] = useState({ x: 100, y: 300 });
  const [offsetY, setOffsetY] = useState(0);

  const handleMovePlayer = (event) => {
    setPlayerPosition((prev) => {
      let newX = prev.x;
      let newY = prev.y;

      switch (event.key) {
        case "ArrowUp":
          newY -= 10;
          break;
        case "ArrowDown":
          newY += 10;
          break;
        case "ArrowLeft":
          newX -= 10;
          break;
        case "ArrowRight":
          newX += 10;
          break;
        default:
          break;
      }

      // Implement scrolling if the player moves up beyond a threshold
      if (newY < 200) {
        setOffsetY((prevOffset) => prevOffset + (200 - newY));
        newY = 200;
      }

      return { x: newX, y: newY };
    });
  };

  useEffect(() => {
    window.addEventListener("keydown", handleMovePlayer);
    return () => {
      window.removeEventListener("keydown", handleMovePlayer);
    };
  }, []);

  return (
    <div id="game-container" style={{ position: "relative", overflow: "hidden", height: "100vh" }}>
      <div
        id="game-world"
        style={{ transform: `translateY(-${offsetY}px)`, transition: "transform 0.1s linear" }}
      >
        <div
          id="player"
          style={{ position: "absolute", left: playerPosition.x, top: playerPosition.y }}
        >
          üèÉ‚Äç‚ôÇÔ∏è
        </div>
        <div id="redoubt" style={{ position: "absolute", bottom: 0, width: "100%" }}>
          üè∞
        </div>
      </div>
    </div>
  );
};

export default GameLoop;
